#pragma once
#include<iostream>


#include<gtest/gtest.h>

namespace {
  template<typename T>
  void print_data(const T* const data, const uint32_t height, const uint32_t width){
    for(uint32_t i = 0; i < height; i++){
      for(uint32_t j = 0; j < width; j++){
        std::cout << int(data[i * width + j]);
        if(j != width - 1){
          std::cout << ", ";
        }
      }
      std::cout << "\n";
    }
  }

  template<typename T, typename T2>
  bool normal_xor (T& a, T2& b){
    return (a || b) && !(a && b);
  }
}

