  EXPECT_EQ(label_error, dicomNodeError_t::SUCCESS);

  uint32_t* spiral_out = new uint32_t[pixels];
  cudaMemcpy(spiral_out, dev_labels, label_size, cudaMemcpyDefault);

  for(int32_t i = 0; i < width * height; i++){
    EXPECT_TRUE(!normal_xor(spiral_out[i], spiral_data[i]));
